interface.signature:
import gsap from 'gsap'
import { ScrollTrigger } from 'gsap/ScrollTrigger'
gsap.registerPlugin(ScrollTrigger)

gsap.from(targets, {
  scrollTrigger: { trigger, start, end, scrub, pin, toggleActions },
  // animation properties
})

interface PlatformConfig {
  name: 'YouTube' | 'TikTok' | 'Instagram' | 'LinkedIn' | 'X' | 'Facebook';
  format: string;      // e.g., "16:9 Widescreen", "9:16 Vertical"
  features: string[];  // 3 bullet points per platform
  iconPath: string;    // SVG icon path (or inline JSX)
}

import Lenis from 'lenis'
const lenis = new Lenis({ lerp: 0.1, duration: 1.2, smoothWheel: true })
lenis.on('scroll', ScrollTrigger.update)
