doc.snippet:
          Pattern 4: Stagger Animation (Gallery Cards)

          useEffect(() => {
            const ctx = gsap.context(() => {
              gsap.from('.style-card', {
                scrollTrigger: {
                  trigger: '.gallery-grid',
                  start: 'top 75%',
                  toggleActions: 'play none none reverse',
                },
                opacity: 0,
                scale: 0.8,
                y: 80,
                stagger: {
                  amount: 1.2,
                  from: 'start',
                  ease: 'power2.inOut',
                },
                duration: 0.6,
                ease: 'back.out(1.2)',
              })
            }, containerRef)

            return () => ctx.revert()
          }, [])

          GSAP Animations:

          // ✅ GOOD: gsap.context() with cleanup
          useEffect(() => {
            const ctx = gsap.context(() => {
              gsap.from(".element", {
                opacity: 0,
                scrollTrigger: { /*...*/ }
              })
            }, containerRef)

            return () => ctx.revert()  // CRITICAL: Prevents memory leaks
          }, [])

